package chap2;
//자료형의 특징
public class VarEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b = (byte)128;
		int i = 128;
		//실수형 리터럴(상수값)의 기본자료형은 double
		float f = 10.1F; //float형 리터럴로 표시
		double d = 10.1;
		/*
		 * 사용되는 접미사 종류(대소문자 상관 없음)
		 * 	L,l : long 형 리터럴 의미
		 * 	F,f : float 형 리터럴 의미
		 * 	D,d : double형 리터럴 의미
		 */
	}

}
